$('.typed-words').each(function (i, el) {
    var $el = $(el);
    var strings = $el.text().split('|');
    var typeSpeed = +$el.attr('data-type-speed') || 50;

    $el
        .typed({
            strings: strings,
            typeSpeed: typeSpeed,
            backDelay: 700,
            loop: true,
            preStringTyped: function () {
                // Avoid content flicker on load
                $el.css({visibility: 'visible'})
            }
        });
});

